    private void jTexCodigoKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_jTexCodigoKeyTyped
        // TODO add your handling code here:
        int longitud = this.jTexCodigo.getText().length() + 1;
        
        if(!Character.isDigit(evt.getKeyChar()) || longitud > 3)
            evt.consume();
    }//GEN-LAST:event_jTexCodigoKeyTyped

    private void jTexNombreKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_jTexNombreKeyTyped
        // TODO add your handling code here:
        int longitud = this.jTexNombre.getText().length() + 1;
        
        if(Character.isDigit(evt.getKeyChar()) || longitud > 45)
            evt.consume();
    }//GEN-LAST:event_jTexNombreKeyTyped

    private void jTexCodigoFocusLost(java.awt.event.FocusEvent evt) {//GEN-FIRST:event_jTexCodigoFocusLost
        // TODO add your handling code here:
        int longitud = this.jTexCodigo.getText().length();
        
        if (longitud < 3){
            this.jTexCodigo.requestFocus();
        }
    }//GEN-LAST:event_jTexCodigoFocusLost

